import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = (props) => {
  /*
  Define state variables for 
  contact info and duplicate check
  */
 const [currentName, setCurrentName] = useState('');
 const [currentPhone, setCurrentPhone] = useState('');
 const [currentEmail, setCurrentEmail] = useState('');
 const [duplicates, setDuplicates] = useState(false);
 const [alert, setAlert] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data
    if the contact name is not a duplicate
    */
   if(!duplicates) {
    props.addContact(currentName, currentPhone, currentEmail);
    setCurrentName('');
    setCurrentPhone('');
    setCurrentEmail('');
   }

  };

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */
 useEffect(() => {
  for(const contactItem of props.contacts) {
    if (contactItem.name === currentName) {
      if(!duplicates) {
        setDuplicates(true);
        setAlert('Contact is already in the list.');
        // console.log('Contact is already in the list.');
      }
      return;
    } else {
      setDuplicates(false);
      setAlert('');
    }
  } 
 }, [props.contacts, currentName, duplicates]);

  return (
    <div>
      <section>
        <h2>Add Contact</h2> 
        <ContactForm 
        name={currentName}
        setName={setCurrentName}
        phone={currentPhone}
        setPhone={setCurrentPhone}
        email={currentEmail}
        setEmail={setCurrentEmail}
        onSubmit={handleSubmit} //handleSubmit or onSubmit?
        
        />
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
      </section>
    </div>
  );
};
